/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
/* ++ ABS-Workorder                                                                                                             ++ */
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
SELECT DISTINCT wo.Workorder, BKo.BestNr, BKo.Datum, Standort.SuchCode AS Lagerstandort, Lagerart.Lagerart, Mitarbei.Name AS [Freigabe-User], BKo.FreigabeZeitpkt AS Freigabezeitpunkt, LiefLsKo.LsNr, LiefLsKo.WeDatum
FROM @Workorder AS wo
JOIN LiefAbKo ON wo.Workorder = RIGHT(REPLICATE(N'0', 10) + LiefAbKo.ABNr, 10)
JOIN LiefAbPo ON LiefAbPo.LiefABKoID = LiefAbKo.ID
JOIN BPo ON LiefAbPo.BPoID = BPo.ID
JOIN BKo ON BPo.BKoID = BKo.ID
JOIN Standort ON BKo.LagerID = Standort.ID
JOIN Lagerart ON BKo.LagerartID = Lagerart.ID
JOIN LiefLsPo ON LiefLsPo.BPoID = BPo.ID
JOIN LiefLsKo ON LiefLsPo.LiefLsKoID = LiefLsKo.ID
JOIN Mitarbei ON BKo.FreigabeMitarbeiID = Mitarbei.ID;

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
/* ++ AdvanTex-Umlagerungsauftrag                                                                                               ++ */
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
SELECT DISTINCT wo.Workorder, BKo.BestNr, BKo.Datum, Standort.SuchCode AS Lagerstandort, Lagerart.Lagerart, Mitarbei.Name AS [Freigabe-User], BKo.FreigabeZeitpkt AS Freigabezeitpunkt, LiefLsKo.LsNr, LiefLsKo.WeDatum
FROM @Workorder AS wo
JOIN LagerBew ON CAST(wo.Workorder AS int) = LagerBew.ID
JOIN EntnPo ON LagerBew.EntnPoID = EntnPo.ID
JOIN EntnKo ON EntnPo.EntnKoID = EntnKo.ID
JOIN Auftrag ON EntnKo.AuftragID = Auftrag.ID
JOIN BKo ON BKo.IntAuftragID = Auftrag.ID
JOIN BPo ON BPo.BKoID = BKo.ID
JOIN LiefLsPo ON LiefLsPo.BPoID = BPo.ID
JOIN LiefLsKo ON LiefLsPo.LiefLsKoID = LiefLsKo.ID
JOIN Standort ON BKo.LagerID = Standort.ID
JOIN Lagerart ON BKo.LagerartID = Lagerart.ID
JOIN Mitarbei ON BKo.FreigabeMitarbeiID = Mitarbei.ID;