BEGIN TRY
  BEGIN TRANSACTION;
  
    UPDATE Kunden SET AnzKopienLS = 0
    WHERE Kunden.KdNr IN (SELECT KdNr FROM @CustomerNoLS)
      AND Kunden.AnzKopienLS != 0;

    UPDATE Vsa SET AnzKopienLS = 0 - Kunden.AnzKopienLS
    FROM Kunden 
    WHERE Vsa.KundenID = Kunden.ID
      AND Kunden.KdNr IN (SELECT KdNr FROM @CustomerNoLS)
      AND Kunden.AnzKopienLS + Vsa.AnzKopienLS != 0;
  
  COMMIT;
END TRY
BEGIN CATCH
  DECLARE @Message varchar(MAX) = ERROR_MESSAGE();
  DECLARE @Severity int = ERROR_SEVERITY();
  DECLARE @State smallint = ERROR_STATE();
  
  IF XACT_STATE() != 0
    ROLLBACK TRANSACTION;
  
  RAISERROR(@Message, @Severity, @State) WITH NOWAIT;
END CATCH;