SELECT IIF(Holding.ID < 0, N'', Holding.Holding) AS Holding, IIF(Holding.ID < 0, N'', Holding.Bez) AS [Holding-Bezeichnung], Kunden.KdNr, Kunden.SuchCode AS Kunde, Bereich.BereichBez AS Warengruppe, Artikel.ArtikelNr AS Artikelnummer, Artikel.ArtikelBez AS Artikelbezeichnung, SUM(RechPo.GPreis) AS [Umsatz netto]
FROM RechPo
JOIN RechKo ON RechPo.RechKoID = RechKo.ID
JOIN Kunden ON RechKo.KundenID = Kunden.ID
JOIN Holding ON Kunden.HoldingID = Holding.ID
JOIN KdArti ON RechPo.KdArtiID = KdArti.ID
JOIN Artikel ON KdArti.ArtikelID = Artikel.ID
JOIN KdBer ON KdArti.KdBerID = KdBer.ID
JOIN Bereich ON KdBer.BereichID = Bereich.ID
