--SELECT KdArti.ID, KdArti.Vorlaeufig, KdArti.WaschPreis, KdArti.LeasPreis, KdArti.Status
UPDATE KdArti SET Vorlaeufig = 0
FROM KdArti
JOIN Kunden ON KdArti.KundenID = Kunden.ID
JOIN Artikel ON KdArti.ArtikelID = Artikel.ID
JOIN @KdArtiVorlaeufig AS KDV ON Kunden.KdNr = KDV.KdNr AND Artikel.ArtikelNr = KDV.ArtikelNr
WHERE KdArti.Vorlaeufig = 1;

