DECLARE @UserID int = (SELECT ID FROM Mitarbei WHERE UserName = N'THALST');

/* Customer Group 1 */
INSERT INTO VsaTexte (KundenID, TextArtID, Memo, VonDatum, BisDatum, Anlage_, AnlageUserID_, UserID_)
SELECT DISTINCT KundenID = Kunden.ID,
  TextArtID = 13,
  Memo = N'Vážené dámy, vážení pánové,

poslední měsíce byly pro Vás, ale i pro naši společnost obzvláště těžké a z hospodářského hlediska znamenaly velkou výzvu. Zdražování v oblasti logistiky, nákupu textilu, ale také v personálním sektoru nebylo možné navzdory plynulé optimalizaci ve výrobě kompenzovat. Rovněž učiněná covidová opatření, stejně jako dopady pandemie vedly ke značným dodatečným nákladům. 
S ohledem na dohodu mezi našimi společnostmi, zvyšujeme doposud platné ceny od 1.2. o 8,75%. 
Žádáme Vás o pochopení pro tato opatření. Jsou základem pro zajištění našich služeb v odpovídající kvalitě i v budoucnosti.

S pozdravem SALESIANER
',
  VonDatum = CAST(N'2022-01-21' AS date),
  BisDatum = CAST(N'2022-05-15' AS date),
  Anlage_ = GETDATE(),
  AnlageUserID_ = @UserID,
  UserID_ = @UserID
FROM Kunden
WHERE Kunden.KdNr IN (SELECT KdNr FROM @CustomerGroup1);

